public class Solution {    public int canCompleteCircuit(int[] gas, int[] cost) {        int sum = 0;        int n = gas.length;        int index = -1;        for (int i = 0; i < 2 * n; i++) {            int j = i % n;            sum += gas[j] - cost[j];            if (sum >= 0 && index == -1)                index = i;            if (sum < 0) {                sum = 0;                index = -1;            }        }        if (index >= 0 && index < n)            return index % n;        else            return -1;    }}